__global__ void calcularDistanciaEuclidiana(float* d_img, float* d_templ, float* d_result, int imgWidth, int templWidth) {
    int idx = blockIdx.x * blockDim.x + threadIdx.x;
    printf("Indice: %d \n", idx);
    if (idx < RESULT_SIZE) {
        int i = idx / (imgWidth - templWidth + 1);
        int j = idx % (imgWidth - templWidth + 1);
        float sum = 0.0;
        for (int m = 0; m < templWidth; ++m) {
            for (int n = 0; n < templWidth; ++n) {
                printf("\nIndice de Imagen: %d", (i + m) * imgWidth + (j + n));
                printf("\nIndice de Template: %d \n", m * templWidth + n);
                float diff = d_img[(i + m) * imgWidth + (j + n)] - d_templ[m * templWidth + n];
                printf("\nValor de diff: %f \n\n", diff);
                sum += diff * diff;
            }
        }
        d_result[idx] = sqrt(sum);
        printf("Resultado %f: en %d y %d \n", d_result[idx], i, j);
    }
}

