    float h_result[RESULT_SIZE];
    //float h_result[81];

    // Matrices en el device
    float* d_img;
    float* d_templ;
    float* d_result;

    std::cout << "Starting declaration for device variables" << std::endl;

    // allocate GPU memory
    cudaMalloc((void**)&d_img, ROWS * COLS * sizeof(int));
    cudaMalloc((void**)&d_templ, TEMPLATE_COLS * TEMPLATE_ROWS * sizeof(int));
    cudaMalloc((void**)&d_result, RESULT_SIZE * sizeof(float));

    cudaMemcpy(d_img, img, ROWS * COLS * sizeof(int), cudaMemcpyHostToDevice);
    cudaMemcpy(d_templ, tmp, TEMPLATE_COLS * TEMPLATE_ROWS * sizeof(int), cudaMemcpyHostToDevice);

    //launch the kernel

    int blockSize = 256;
    int gridSize = (RESULT_SIZE + blockSize - 1) / blockSize;
    calcularDistanciaEuclidiana << <gridSize, blockSize >> > (d_img, d_templ, d_result, COLS, TEMPLATE_COLS);

    // Copiar el resultado al host
    cudaMemcpy(h_result, d_result, RESULT_SIZE * sizeof(float), cudaMemcpyDeviceToHost);

    double min = 999999;
    int coordsX, coordsY;
    for (int i = 0; i < RESULT_SIZE; ++i) {
        if (h_result[i] <= min) {
            min = h_result[i];
            coordsX = i / (COLS - TEMPLATE_COLS + 1);
            coordsY = i % (COLS - TEMPLATE_COLS + 1);
        }
    }
    printf("Match perfecto encontrado en las coordenadas (%d, %d), medida minima es: %f\n", coordsX, coordsY, min);

    // Liberar memoria en el device
    cudaFree(d_img);
    cudaFree(d_templ);
    cudaFree(d_result);

    return 0;

